mapper.select:
		SELECT LOSENO, TITLE, CONTENT, LOSEDATE, SPACENO, TYPE, UPLOADER
		FROM LOSE
        where loseNo = #{ loseNo }

		SELECT loseFileNo, savedFileName, loseNo, userfileName
		FROM LOSEFILE
        where loseNo = #{ loseNo }

mapper.update:
UPDATE LOSE
SET title =#{title}, content=#{content}
WHERE loseNo = #{loseNo}

mapper.select:
SELECT loseNo, title, content
FROM NOTICE
WHERE loseNo = #{ loseNo }
