mapper.select:
select  
from space
where spaceno = #{ spaceNo }

select   
from spacefile
where spacefileno = #{ spaceFileNo }

select  
from space
where hostNo = #{ hostNo }

mapper.insert:

	SELECT SPACE_SEQUENCE.CURRVAL FROM DUAL

INSERT INTO SPACE(
	spaceNo, spaceName, content, maximum, bim, smoking, food, wifi, computer, hostNo, hostId
)
VALUES
(
	SPACE_SEQUENCE.NEXTVAL
	, #{ spaceName }
	, #{ content }
	, #{ maximum }
	, #{ bim }
	, #{ smoking }
	, #{ food }
	, #{ wifi }
	, #{ computer }
	, #{ hostNo }
	, #{ hostId }
)

INSERT INTO SpaceFILE(
	
)
VALUES
(
	SPACEFILE_SEQUENCE.NEXTVAL
	, #{ spaceNo }
	, #{ savedFileName }
	, #{ flag }
)

mapper.update:
update space
set spaceName = #{ spaceName }
	, content = #{ content }
	, maximum = #{ maximum }
	, bim = #{ bim }
	, smoking = #{ smoking }
	, food = #{ food }
	, wifi = #{ wifi }
	, computer = #{ computer }
where spaceNo = #{ spaceNo }

update spacefile
set savedFileName = #{ savedFileName }
where spaceNo = #{ spaceNo } and flag = 1

